mapper.insert:
insert into ${tableName} (activity_id, component_id, guid, limited_type)
values (
         #{entity.activityId,jdbcType=VARCHAR},
         #{entity.componentId,jdbcType=BIGINT},
         #{entity.guid,jdbcType=BIGINT},
         #{entity.limitedType,jdbcType=INTEGER}
       )
  ON DUPLICATE KEY
UPDATE total_count = #{entity.totalCount}, used_count = #{entity.usedCount};

mapper.select:
SELECT id, activity_id, component_id, guid, limited_type,
       total_count, used_count, create_time, update_time
FROM ${tableName}
WHERE `activity_id` = #{activityId} AND `component_id` = #{componentId}
AND `guid` = #{guid} AND `limited_type` = #{limitedType}
limit 1

mapper.update:
UPDATE ${tableName}
SET `total_count` = `total_count` + #{addTotalCount}
WHERE `activity_id` = #{activityId} AND `component_id` = #{componentId}
AND `guid` = #{guid} AND `limited_type` = #{limitedType}

UPDATE ${tableName}
SET `used_count` = `used_count` + #{addUsedCount}
WHERE `activity_id` = #{activityId} AND `component_id` = #{componentId}
AND `guid` = #{guid} AND `limited_type` = #{limitedType}
